function initialize_stage()
    -- reinitialize tables for everything
	colliders = {}
	camera = {}
	decors = {}
	martians = {}
	martianbullets = {}
	players = {}
	playerbullets = {}
	powerups = {}

    -- load current stage
	map=stages[stagenum]

    -- map variables
	map_w = #map[1] -- Obtains the width of the first row of the map
	map_h = #map -- Obtains the height of the map
	map_x = 0
	map_y = 0
	target_map_x = 0
	target_map_y = 0
	map_display_buffer = 2  -- We have to buffer one tile before and behind our viewpoint.
                            -- Otherwise, the tiles will just pop into view, and we don't want that.

    -- play music for levels
    if music_title:isPlaying() == true then
        music_title:stop()
    end
    if music_ending:isPlaying() == true then
        music_ending:stop()
    end
    if music_gover:isPlaying() == true then
        music_gover:stop()
    end
    if stagenum >= 1 and stagenum <= 4 then
        if music4:isPlaying() == true then
            music4:stop()
        end
        if music1:isPlaying() == false then
            music1:setLooping(true)
	        music1:play()
        end
    end
    if stagenum == 5 or stagenum == 6 then
        if music1:isPlaying() == true then
            music1:stop()
        end
	    if music2:isPlaying() == false then
            music2:setLooping(true)
	        music2:play()
        end
    end
    if stagenum == 7 or stagenum == 8 then
        if music2:isPlaying() == true then
            music2:stop()
        end
	    if music3:isPlaying() == false then
            music3:setLooping(true)
	        music3:play()
        end
    end
    if stagenum == 9 then
        if music3:isPlaying() == true then
            music3:stop()
        end
	    if music4:isPlaying() == false then
            music4:setLooping(true)
	        music4:play()
        end
    end

    -- spawn decors & Martians
	spawn_decors()
	spawn_martians()
	spawn_players()
	spawn_powerups()
	spawn_colliders()
end